import React, { useEffect, useState } from 'react';
import { Box, Container, Typography } from '@mui/material';
import { useValue } from '../context/ContextProvider';

const ManageRooms = () => {
  const {
    state: { rooms, currentUser },
  } = useValue();

  const [userRooms, setUserRooms] = useState([]);

  useEffect(() => {
    // Filter rooms to show only the current user's rooms
    setUserRooms(rooms.filter((room) => room.ownerId === currentUser?.id));
  }, [rooms, currentUser]);

  return (
    <Container sx={{ my: 4 }}>
      <Typography variant="h4" component="h2" gutterBottom>
        Manage Rooms
      </Typography>
      {userRooms.length > 0 ? (
        userRooms.map((room) => (
          <Box key={room._id} sx={{ mb: 2, p: 2, border: '1px solid gray' }}>
            <Typography variant="h6">{room.title}</Typography>
            <Typography variant="body1">{room.description}</Typography>
            <Typography variant="body2">${room.price}</Typography>
          </Box>
        ))
      ) : (
        <Typography variant="body1">No rooms found</Typography>
      )}
    </Container>
  );
};

export default ManageRooms;
