import React, { useState } from 'react';
import { AppBar, Box, Button, Container, IconButton, Toolbar, Typography } from '@mui/material';
import { Lock, Menu } from '@mui/icons-material';
import { useValue } from '../context/ContextProvider';
import UserIcons from './user/UserIcons';
import Sidebar from './sidebar/Sidebar';
import { Link } from 'react-router-dom';

const NavBar = () => {
  const {
    state: { currentUser },
    dispatch,
  } = useValue();

  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <AppBar>
        <Container maxWidth="lg">
          <Toolbar disableGutters>
            <Box sx={{ mr: 1 }}>
              <IconButton size="large" color="inherit" onClick={() => setIsOpen(true)}>
                <Menu />
              </IconButton>
            </Box>
            <Typography variant="h6" component="h1" noWrap sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
              You Are Welcome
            </Typography>
            <Typography variant="h6" component="h1" noWrap sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
              YRW
            </Typography>
            {currentUser ? (
              <>
                <Button color="inherit" component={Link} to="/manage-rooms">
                  Manage Rooms
                </Button>
                <UserIcons />
              </>
            ) : (
              <Button color="inherit" startIcon={<Lock />} onClick={() => dispatch({ type: 'OPEN_LOGIN' })}>
                Login
              </Button>
            )}
          </Toolbar>
        </Container>
      </AppBar>
      <Toolbar />
      <Sidebar {...{ isOpen, setIsOpen }} />
    </>
  );
};

export default NavBar;
