import React from 'react';
import { Box, Button, Container, Stack, Typography } from '@mui/material';
import { useValue } from '../context/ContextProvider';

const ManageRooms = () => {
  const {
    state: { rooms, currentUser },
    dispatch,
  } = useValue();

  const handleDelete = (roomId) => {
    // Implement delete room logic here
  };

  const handleUpdate = (roomId, updatedDetails) => {
    // Implement update room logic here
  };

  // Filter rooms to show only the current user's rooms
  const userRooms = rooms.filter(room => room.ownerId === currentUser.id);

  return (
    <Container sx={{ my: 4 }}>
      <Typography variant="h4" component="h2" gutterBottom>
        Manage Rooms
      </Typography>
      {userRooms.map((room) => (
        <Box key={room._id} sx={{ mb: 2, p: 2, border: '1px solid gray' }}>
          <Typography variant="h6">{room.title}</Typography>
          <Typography variant="body1">{room.description}</Typography>
          <Typography variant="body2">${room.price}</Typography>
          <Stack direction="row" spacing={2}>
            <Button
              variant="contained"
              color="primary"
              //onClick={() => handleUpdate(room._id, { title: 'New Title' })} // Example update
            >
              Update
            </Button>
            <Button
              variant="contained"
              color="secondary"
              //onClick={() => handleDelete(room._id)}
            >
              Delete
            </Button>
          </Stack>
        </Box>
      ))}
    </Container>
  );
};

export default ManageRooms;
