import React, { useEffect } from 'react';
import { Box, Button, Container, Stack, TextField, Typography } from '@mui/material';
import { useValue } from '../../context/ContextProvider';
import { deleteRoom, updateRoom } from '../../actions/room';

const UpdateDeleteRoom = () => {
  const {
    state: { rooms, currentUser },
    dispatch,
  } = useValue();

  useEffect(() => {
    // Fetch rooms from the server if not already fetched
    // dispatch(fetchRooms(currentUser));
  }, [dispatch, currentUser]);

  const handleDelete = (roomId) => {
    deleteRoom(roomId, currentUser, dispatch);
  };

  const handleUpdate = (roomId, updatedDetails) => {
    updateRoom(roomId, updatedDetails, currentUser, dispatch);
  };

  return (
    <Container sx={{ my: 4 }}>
      <Typography variant="h4" component="h2" gutterBottom>
        Manage Rooms
      </Typography>
      {rooms.map((room) => (
        <Box key={room._id} sx={{ mb: 2, p: 2, border: '1px solid gray' }}>
          <Typography variant="h6">{room.title}</Typography>
          <Typography variant="body1">{room.description}</Typography>
          <Typography variant="body2">${room.price}</Typography>
          <Stack direction="row" spacing={2}>
            <Button
              variant="contained"
              color="primary"
              onClick={() => handleUpdate(room._id, { title: 'New Title' })} // Example update
            >
              Update
            </Button>
            <Button
              variant="contained"
              color="secondary"
              onClick={() => handleDelete(room._id)}
            >
              Delete
            </Button>
          </Stack>
        </Box>
      ))}
    </Container>
  );
};

export default UpdateDeleteRoom;
